import './App.css';
import React, { useEffect, useState } from 'react';
import ButtonAppBar from './ButtonAppBar';
import CheckboxList from './CheckboxList';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import CommentIcon from '@material-ui/icons/Comment';

import SimpleContainer from './SimpleContainer';
import {Container, Card, Paper} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));


function App() {

  const initialData = [
    {
      question: "Nel 2050?",
      answerOptions: [
      {
        answer: "Saremo tutti vegani",
        correct: false,
        checked: false
      },{
        answer: "I più fortunati però potranno mangiare gli insetti",
        correct: false,
        checked: false
      },{
        answer: "Saremo tantissimi ",
        correct: true,
        checked: false
      },{
        answer: "Saremo tutti un pochino più vecchi",
        correct: false,
        checked: false
      }]},{
      question: "Nel 2050?",
      answerOptions: [
      {
        answer: "Saremo tutti vegani",
        correct: true,
        checked: false
      },{
        answer: "I più fortunati però potranno mangiare gli insetti",
        correct: false,
        checked: false
      },{
        answer: "Saremo tantissimi ",
        correct: false,
        checked: false
      },{
        answer: "Saremo tutti un pochino più vecchi",
        correct: false,
        checked: false
      }]}
  ];

  const [data, setData] = useState([])
  const [dataAlustettu, setDataAlustettu] = useState(false)

  useEffect(() => {
    let jemma = window.localStorage;
    let tempData = JSON.parse(jemma.getItem("data"))
    if (tempData == null) {
      jemma.setItem("data", JSON.stringify(initialData))
      tempData = initialData
    } 
    setData(tempData);
    setDataAlustettu(true)
  }, [])

  useEffect(() => {
    if (dataAlustettu) {
      window.localStorage.setItem("data", JSON.stringify(data))
    }
  }, [data])

  const classes = useStyles();
  const [checked, setChecked] = React.useState([0]);

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];
    value.checked = true;

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
    console.log(newChecked)
  };

  const createQuestion = (value, index) => {
    return(
        <List className={classes.root}>
        <p className="question">{value.question}</p>
        {value.answerOptions.map((value, index) => {
          const labelId = `checkbox-list-label-${value}`;
          console.log(value.checked)
          return(
          <ListItem key={index} role={undefined} dense >
          <ListItemIcon>
            <Checkbox
              onClick={handleToggle(value)}
              checked={value.checked}
              edge="start"
              tabIndex={-1}
              inputProps={{ 'aria-labelledby': labelId
             }}
            />
          </ListItemIcon>
          <ListItemText id={index} primary={value.answer} />
        </ListItem>
          )
        })}
      </List>
    );
  }


  return (
    <div>
      <ButtonAppBar/>
      <Container className="quizContainer">
      <Paper elevation={3}>
      {initialData.map((value, index) => {
        return (createQuestion(value, index))
      })}<br/>
      </Paper>
      </Container>
    </div>
  );
}

export default App;
