import './App.css';
import React, { useEffect, useState } from 'react';
import ButtonAppBar from './ButtonAppBar';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';

import {Container, Card, Paper, Button} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '100%',
    backgroundColor: theme.palette.background.paper,
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));


function App() {

  const initialData = [{
    quiz: [ 
      {
      question: "How is sepiolite also called?",
      answerOptions: [
      {
        answer: "Montmonite",
        correct: false,
        selected: false
      },{
        answer: "Calcite",
        correct: false,
        selected: false
      },{
        answer: "Meerschaum",
        correct: true,
        selected: false
      },{
        answer: "Silica gel",
        correct: false,
        selected: false
      }]},
      {
      question: "How is sepiolite commonly mined?",
      answerOptions: [
      {
        answer: "Surface mining",
        correct: true,
        selected: false
      },{
        answer: "Shaft mining",
        correct: false,
        selected: false
      },{
        answer: "In situ mining",
        correct: false,
        selected: false
      },{
        answer: "Fracking",
        correct: false,
        selected: false
      }]},{
      question: "Which country is the largest sepiolite producer in the world?",
      answerOptions: [
      {
        answer: "USA",
        correct: true,
        selected: false
      },{
        answer: "Tanzania",
        correct: false,
        selected: false
      },{
        answer: "Spain ",
        correct: true,
        selected: false
      },{
        answer: "Turkey",
        correct: false,
        selected: false
      }]},{
      question: "Which state holds the largest bentonite mine in the USA?",
      answerOptions: [
      {
        answer: "Kentucky",
        correct: true,
        selected: false
      },{
        answer: "Ohio",
        correct: false,
        selected: false
      },{
        answer: "Wisconsin ",
        correct: false,
        selected: false
      },{
        answer: "Wyoming",
        correct: true,
        selected: false
      }]}
  ],
  quiz: [
    {
      question: "How is sepiolite also called?",
      answerOptions: [
      {
        answer: "Montmonite",
        correct: false,
        selected: false
      },{
        answer: "Calcite",
        correct: false,
        selected: false
      },{
        answer: "Meerschaum",
        correct: true,
        selected: false
      },{
        answer: "Silica gel",
        correct: false,
        selected: false
      }]},
      {
      question: "How is sepiolite commonly mined?",
      answerOptions: [
      {
        answer: "Surface mining",
        correct: true,
        selected: false
      },{
        answer: "Shaft mining",
        correct: false,
        selected: false
      },{
        answer: "In situ mining",
        correct: false,
        selected: false
      },{
        answer: "Fracking",
        correct: false,
        selected: false
      }]},{
      question: "Which country is the largest sepiolite producer in the world?",
      answerOptions: [
      {
        answer: "USA",
        correct: true,
        selected: false
      },{
        answer: "Tanzania",
        correct: false,
        selected: false
      },{
        answer: "Spain ",
        correct: true,
        selected: false
      },{
        answer: "Turkey",
        correct: false,
        selected: false
      }]},{
      question: "Which state holds the largest bentonite mine in the USA?",
      answerOptions: [
      {
        answer: "Kentucky",
        correct: true,
        selected: false
      },{
        answer: "Ohio",
        correct: false,
        selected: false
      },{
        answer: "Wisconsin ",
        correct: false,
        selected: false
      },{
        answer: "Wyoming",
        correct: true,
        selected: false
      }]}
  ]
}];

  const [data, setData] = useState([])
  const [dataAlustettu, setDataAlustettu] = useState(false)

  useEffect(() => {
    let jemma = window.localStorage;
    let tempData = JSON.parse(jemma.getItem("data"))
    if (tempData == null) {
      jemma.setItem("data", JSON.stringify(initialData))
      tempData = initialData
    } 
    setData(tempData);
    setDataAlustettu(true)
  }, [])

  useEffect(() => {
    if (dataAlustettu) {
      window.localStorage.setItem("data", JSON.stringify(data))
    }
  }, [data])

  const classes = useStyles();
  const [checked, setChecked] = React.useState([0]);

  const handleToggle = (event, index, parentIndex) => {
    let deepCopy = JSON.parse(JSON.stringify(data))
    deepCopy[parentIndex].answerOptions[index].selected = !deepCopy[parentIndex].answerOptions[index].selected;
    setData(deepCopy)
  };



  return (
    <div>
      <ButtonAppBar/>
        <Container className="quizContainer">
        <div className={classes.root}>

        <Button variant="outlined">Mining quiz</Button> 
        <Button variant="outlined">Other quiz</Button>
        </div>
        <br/>
            {data.map((value, parentIndex) => {

              return(
                <div className="questionCard">
                <Card elevation={1}>
                <List className={classes.root}>
                <p className="question">{value.question}</p>
                {value.answerOptions.map((value, index) => {
                  return(
                  <ListItem key={index} role={undefined} dense >
                    <ListItemIcon>
                      <Checkbox
                        onChange={(event) => handleToggle(event, index, parentIndex)}
                        checked={value.correct}
                        edge="start"
                        tabIndex={-1}
                        disabled
                        disableRipple
                      />
                    </ListItemIcon>
                    <ListItemIcon>
                      <Checkbox
                        onChange={(event) => handleToggle(event, index, parentIndex)}
                        checked={value.selected}
                        edge="start"
                        tabIndex={-1}
                      />
                    </ListItemIcon>
                    <ListItemText id={index} primary={value.answer} />
                  </ListItem>
                  )
                })}
                </List>
                </Card>
                </div>
              );
            })}<br/>
        </Container>
    </div>
  );
}

export default App;
