import './App.css';
import React, { useEffect, useState } from 'react';
import ButtonAppBar from './ButtonAppBar';
import CheckboxList from './CheckboxList';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import CommentIcon from '@material-ui/icons/Comment';

import SimpleContainer from './SimpleContainer';
import Container from '@material-ui/core/Container';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));


function App() {

  const initialData = [
    {
      question: "hello",
      questionId: 0,
      answerOptions: [
      {
        answer: "moi0",
        correct: "false",
        checked: "false"
      },
      {
        answer: "moi1",
        correct: "false",
        checked: "false"
      },
      {
        answer: "moi2",
        correct: "false",
        checked: "false"
      },
      {
        answer: "moi3",
        correct: "false",
        checked: "false"
      }
      ]},
      {
      question: "world",
      questionId: 1,
      answerOptions: [{
        answer: "moi0",
        correct: "false",
        checked: "false"
      },
      {
        answer: "moi1",
        correct: "false",
        checked: "false"
      },
      {
        answer: "moi2",
        correct: "false",
        checked: "false"
      },
      {
        answer: "moi3",
        correct: "false",
        checked: "false"
      }],
    }
  ];

  const [data, setData] = useState([])
  const [dataAlustettu, setDataAlustettu] = useState(false)

  useEffect(() => {
    let jemma = window.localStorage;
    let tempData = JSON.parse(jemma.getItem("data"))
    if (tempData == null) {
      jemma.setItem("data", JSON.stringify(initialData))
      tempData = initialData
    } 
    setData(tempData);
    setDataAlustettu(true)
  }, [])

  useEffect(() => {
    if (dataAlustettu) {
      window.localStorage.setItem("data", JSON.stringify(data))
    }
  }, [data])



  const classes = useStyles();
  const [checked, setChecked] = React.useState([0]);

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
    console.log(newChecked)
  };

  const createQuestion = (value, labelId) => {
    return(
      <List className={classes.root}>
      <h3>{value.question}</h3>
      {value.answerOptions.map((value) => {
        return(
        <ListItem key={value.id} role={undefined} dense button onClick={handleToggle(value)}>
        <ListItemIcon>
          <Checkbox
            edge="start"
            checked={checked.indexOf(value.id) !== -1}
            tabIndex={-1}
            disableRipple
            inputProps={{ 'aria-labelledby': labelId }}
          />
        </ListItemIcon>
        <ListItemText id={labelId} primary={`Line item ${value + 1}`} />
      </ListItem>
        )
      })}
    </List>

    );
  }


  return (
    <div>
      <ButtonAppBar/>
      {initialData.map((value) => {
        const labelId = `checkbox-list-label-${value}`;
        return (createQuestion(value, labelId))
      })}
    </div>
  );
}

export default App;
