import './App.css';
import React, { useEffect, useState } from 'react';
import ButtonAppBar from './ButtonAppBar';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import quizzesImported from './Quizzes'
import {Container, Paper, Button, LinearProgress, Switch, FormControlLabel, TextField} from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '100%',
    backgroundColor: theme.palette.background.paper,
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

function App() {
  const initialData = quizzesImported;

  const classes = useStyles();
  const [data, setData] = useState([])
  const [dataAlustettu, setDataAlustettu] = useState(false)
  const [quiz, setQuiz] = useState(0);
  const [answersVisible, setAnswersVisible] = useState(false)
  const [isTeacherView, setIsTeacherView] = useState(false)

  useEffect(() => {
    let jemma = window.localStorage;
    let tempData = JSON.parse(jemma.getItem("data"))
    if (tempData == null) {
      jemma.setItem("data", JSON.stringify(initialData))
      tempData = initialData
    } 
    setData(tempData);
    setDataAlustettu(true)
  }, [])

  useEffect(() => {
    if (dataAlustettu) {
      window.localStorage.setItem("data", JSON.stringify(data))
    }
  }, [data])

  const handleToggle = (event, index, parentIndex) => {
    let deepCopy = JSON.parse(JSON.stringify(data))
    deepCopy[quiz].quizQuestions[parentIndex].answerOptions[index].selected = !deepCopy[quiz].quizQuestions[parentIndex].answerOptions[index].selected;
    setData(deepCopy)
  };

  const selectQuiz = (quizNo) => {
    setQuiz(quizNo);
  }

  const toggleAnswers = () => {
    setAnswersVisible(!answersVisible);
  }

  // --------------------------------------
  const addNewQuestion= (quizIndex) => {
    quizIndex = 0;
    console.log("add new")
    let deepCopy = JSON.parse(JSON.stringify(data))
    let newQuestion= {question: "", answerOptions: [ {answer: "", correct: "", selected: ""}] }
    deepCopy[quizIndex].quizQuestions.push(newQuestion) 
    setData(deepCopy)
  }

  const answerChanged = () => {
    console.log("answerChanged")
  }

  const deleteAnswer = () => {
    console.log("answer deleted")
  }
  // --------------------------------------

  const [status, setStatus] = React.useState({
    teacherMode: true,
  });

  const handleChange = (event) => {
    setStatus({ ...status, [event.target.name]: event.target.checked });
  };

  return (
    <div>
      <ButtonAppBar/>
      <Container className="quizContainer">
        <div className={classes.root}>
        <FormControlLabel
        control={
          <Switch
          checked={status.teacherMode}
          onChange={handleChange}
          name="teacherMode"
          inputProps={{ 'aria-label': 'secondary checkbox' }}
        />
        }
        label="Teacher mode"/><br/>
        {data.map((val, index) => {
          return (
            <Button variant="outlined" onClick={() => selectQuiz(index)}>{val.quizName}</Button>
          )
        })}
        </div>
            {dataAlustettu ? data[quiz].quizQuestions.map((value, parentIndex) => {
              return(
                <div className="questionCard">
                <Paper elevation={1}>
                <List className={classes.root}>
                <p className="question">{value.question}</p>
                {value.answerOptions.map((value, index) => {
                  return(
                  <ListItem key={index} role={undefined} dense >
                   { answersVisible ? <ListItemIcon>
                      <Checkbox
                        onChange={(event) => handleToggle(event, index, parentIndex)}
                        checked={value.correct}
                        edge="start"
                        tabIndex={-1}
                        hidden={answersVisible}
                        disabled
                      />
                    </ListItemIcon> : null }
                    <ListItemIcon>
                      <Checkbox
                        onChange={(event) => handleToggle(event, index, parentIndex)}
                        checked={value.selected}
                        edge="start"
                        tabIndex={-1}
                      />
                    </ListItemIcon>
                    {status.teacherMode ?  
                      <div>
                        <TextField id="outlined-basic" onChange={answerChanged} size="small" label={"Answer " + (index+1)} variant="outlined" value={value.answer}/>
                        <Button className="deleteButton" onClick={deleteAnswer}>Delete</Button> 
                      </div>
                      :  <ListItemText id={index} primary={value.answer} />
                    }
                  </ListItem>
                  )
                })}
                </List>
                </Paper>
                </div>
              );
            }): "" }<p>
            {status.teacherMode ? <Button className="addButton" onClick={addNewQuestion}>Add new question</Button> : ""}
            </p>
            <div className="bottomButtons">
              <Button variant="contained" onClick={() => toggleAnswers()}>{answersVisible ? "Hide answers" : "Show answers"}</Button> <br />
            </div>
      </Container>
    </div>
  );
}

export default App;
