import './App.css';
import React, { useEffect, useState } from 'react';
import ButtonAppBar from './ButtonAppBar';
import CheckboxList from './CheckboxList';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import CommentIcon from '@material-ui/icons/Comment';

import {Container, Card, Paper} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));


function App() {

  const initialData = [
    {
      question: "How is sepiolite commonly mined?",
      answerOptions: [
      {
        answer: "Surface mining",
        correct: true,
        selected: false
      },{
        answer: "Shaft mining",
        correct: false,
        selected: false
      }]},{
      question: "What is the largest sepiolite producer in the world?",
      answerOptions: [
      {
        answer: "USA",
        correct: true,
        selected: false
      },{
        answer: "Tanzania",
        correct: false,
        selected: false
      },{
        answer: "Saremo tantissimi ",
        correct: false,
        selected: false
      },{
        answer: "Saremo tutti un pochino più vecchi",
        correct: false,
        selected: false
      }]}
  ];

  const [data, setData] = useState([])
  const [dataAlustettu, setDataAlustettu] = useState(false)

  useEffect(() => {
    let jemma = window.localStorage;
    let tempData = JSON.parse(jemma.getItem("data"))
    if (tempData == null) {
      jemma.setItem("data", JSON.stringify(initialData))
      tempData = initialData
    } 
    setData(tempData);
    setDataAlustettu(true)
  }, [])

  useEffect(() => {
    if (dataAlustettu) {
      window.localStorage.setItem("data", JSON.stringify(data))
    }
  }, [data])

  const classes = useStyles();
  const [checked, setChecked] = React.useState([0]);

  const handleToggle = (event, index, parentIndex) => {
    let deepCopy = JSON.parse(JSON.stringify(data))
    deepCopy[parentIndex].answerOptions[index].selected = !deepCopy[parentIndex].answerOptions[index].selected;
    setData(deepCopy)
  };



  return (
    <div>
      <ButtonAppBar/>
        <Container className="quizContainer">
          <Paper elevation={3}>
            {data.map((value, parentIndex) => {
              return(
                <List className={classes.root}>
                <p className="question">{value.question}</p>
                {value.answerOptions.map((value, index) => {
                  return(
                  <ListItem key={index} role={undefined} dense >
                    <ListItemIcon>
                      <Checkbox
                        onChange={(event) => handleToggle(event, index, parentIndex)}
                        checked={value.selected}
                        edge="start"
                        tabIndex={-1}
                      />
                    </ListItemIcon>
                    <ListItemText id={index} primary={value.answer} />
                  </ListItem>
                  )
                })}
                </List>
              );
            })}<br/>
          </Paper>
        </Container>
    </div>
  );
}

export default App;
