import React, { useState, useEffect, useReducer } from 'react';
import './App.css';
import {
    Chart,
    BarSeries,
    Title,
    ArgumentAxis,
    ValueAxis,
} from '@devexpress/dx-react-chart-material-ui';
import TextField from '@material-ui/core/TextField'
import { Animation } from '@devexpress/dx-react-chart';
const data = [
    { aihealue: 'ATK:n perusteet', Points: 5, MaxPoints: 10 },
    { aihealue: 'Biologian alkeet', Points: 2, MaxPoints: 10 },
    { aihealue: 'Matikkan lopputentti', Points: 10, MaxPoints: 10 },
    { aihealue: 'Kemian pääsykoe', Points: 8, MaxPoints: 9 },
    { aihealue: 'Biologian jatkoa ', Points: 5, MaxPoints: 10 },
    { aihealue: 'Terveystiedon alkeet', Points: 9, MaxPoints: 10 },
];
export default class Charts extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            data,
        };
    }


    render() {
        const { data: chartData } = this.state;
        return (
            <div className="App">
                <Chart
                    data={data}
                >
                    <ArgumentAxis />
                    <ValueAxis max={3} />
                    <BarSeries
                        valueField="Points"
                        argumentField="aihealue"
                    />

                    <Title text="Testi Aihealueet" />
                    <Animation />
                </Chart>
                <div>
                    {data.map((value, index))}
                <TextField
                    id="Points"
                    label="Number"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="Number"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="Number"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="Number"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="Number"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="Number"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />
                </div>
                <div>
                <TextField
                    id="Points"
                    label="MaxNumber"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="MaxNumber"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="MaxNumber"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="MaxNumber"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="MaxNumber"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />  <TextField
                    id="Points"
                    label="MaxNumber"
                    type="number"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />
                </div>
            </div>
            );
    }
}
